mapper.update:
update knowledge
set Knowledge_name = #{knowledgeName,jdbcType=VARCHAR},
  Subject_ID = #{subjectId,jdbcType=INTEGER},
  Superior_ID = #{superiorId,jdbcType=INTEGER}
where Knowledge_ID = #{knowledgeId,jdbcType=INTEGER}

UPDATE knowledge
SET isDelete = 1
WHERE Knowledge_ID = #{knowledgeId,jdbcType=INTEGER}
